val pvd = s"${processor_name}__${processor_version}__${yrweek}"

def writeVersionColumn(sc:SparkContext, column: String, coltype: String, table: String, keyspace: String) =
{

 val cassTable = sc.cassandraTable(keyspace, table)
 val cassSchema = cassTable.selectedColumnNames.toSet

 if(!cassSchema.contains(column)) {
      val cassTable = sc.cassandraTable(keyspace, table)
      cassTable.connector.withSessionDo {
            session => {
              session.execute(s"ALTER TABLE $keyspace.$table ADD $column $coltype")
    } } }
 }

writeVersionColumn(sc, v, "text", "news", "processed_news")
writeVersionColumn(sc, pvd, "frozen<set<frozen<place>>>", "news", "processed_news")

