"""
Alternate NLTK tag methods
Currently being used but not as good as Stanford tagger
"""

nospace_langs = ('zh',)

def get_nltk_gpes(text):
    """
    Use standard NLTK tagger (sucks)
    """
    try:
        sents = nltk.sent_tokenize(re.sub('[\:\\n]','.',text))
    except LookupError:
        print('Download nltk data')
        download(['punkt','maxent_treebank_pos_tagger','averaged_perceptron_tagger','maxent_ne_chunker','words'])
        sents = nltk.sent_tokenize(re.sub('[\:\\n]','.',text))
    gpes = []
    for sent in sents:
        tokenizer = nltk.RegexpTokenizer('\w(?:[-\w]*[\'\w]*\w)?')
        tokens = tokenizer.tokenize(sent.replace(',', '.'))
        tagged_text = nltk.pos_tag(tokens)
        for subtree in nltk.ne_chunk(tagged_text).subtrees():
            # State/country codes often end up labelled as 'ORGANIZATION'
            if subtree.label() in ['GPE', 'ORGANIZATION', 'PERSON']:
                gpe = u' '.join([t[0] for t in subtree])
                if len(gpe) > 2 or (len(gpe) > 1 and gpe.isupper()):
                    gpes.append(gpe)
            elif subtree.label() == 'S':
                for t in subtree:
                    if type(t) == tuple and t[0] and t[1].startswith('NNP'):
                        gpe = u' '.join([t[0]])
                        if len(gpe) > 2 or (len(gpe) > 1 and gpe.isupper()):
                            gpes.append(gpe)
    return gpes

def dumb_gpes(text, max=10):
    """
    Split text by spaces, stripping non-alphanumeric characters,
    and return the resulting list
    """
    words = re.sub(r',|\'|"|:', ' ', text)
    words = filter(unicode.isalnum, words.split())
    words = [word for word in words if len(word) > 2]
    return words[0:max]


def splitchars(text, maxwords=20, maxlen=5, minlen=2):
    """
    Intended for Asian languages lacking spaces between words,
    this function splits text at intervals producing a list of
    words each containing minlen to maxlen characters.
    """
    text = filter(unicode.isalnum, text)
    words = set()
    texts = text.split()
    for t in texts:
        for x in range(len(t)):
            if len(words) >= maxwords:
                break
            for y in range(maxlen, 0, -1):
                if len(words) >= maxwords:
                    break
                if x + y <= len(t) and ((x + y) - x) >= minlen:
                    words.add((t[x:x + y]))
    return list(words)

