"""
geocode match Scoring methods
"""

def relation_score(places, admins, countries):
    """
    Adjust scores if the admin/country name or code of a place/admin match
    equals the name/code of an admin/country match.
    """
    for place in places.keys():
        for item in places[place]:
            admin_code = item['admin1']
            country = item['country']
            if admin_code:
                if admin_code.upper() in admins.keys():
                    item['score'] *= 3
                    for admitem in admins[admin_code]:
                        if admitem['country'] == country:
                            admitem['score'] *= 3
                else:
                    for admin in admins.keys():
                        for admitem in admins[admin]:
                            if (admitem['country'] == country and
                                        admitem['admin1'] == admin_code and
                                        admitem['feature_code'] == 'ADM1'):
                                item['score'] *= 4
                                admitem['score'] *= 3
            if country:
                for cc in countries.keys():
                    for ccitem in countries[cc]:
                        if ccitem['country'] == country:
                            ccitem['score'] *= 2
                            if ccitem['matching_word'] != item['matching_word'] \
                                    and item['matching_word'] not in (admins.keys() + countries.keys()):
                                item['score'] *= 2
    for adm in admins.keys():
        for item in admins[adm]:
            country = item['country']
            if country:
                for cc in countries.keys():
                    for ccitem in countries[cc]:
                        if ccitem['country'] == country:
                            if ccitem['matching_word'] != item['matching_word'] \
                                    and item['matching_word'] in admins.keys():
                                item['score'] *= 2



def score_matches(matches, name, codes):
    """
    Make a semi-educated guess about which match is better
    """
    for nmatch in matches:
        fc = nmatch['feature_code']
        alternates = nmatch['alternate'] or []
        score = 1
        exact_match = False
        if nmatch['name'].lower() == name.lower():
            # Best type of nmatch
            score += 100000
            exact_match = True
        if name not in codes and (nmatch['name'].lower().startswith(name.lower()) or nmatch['name'].lower().endswith(name.lower())):
            score += 50000
            if fc.startswith('PCL'):
                score += 1000000
        elif name.lower() in (
            [f.lower() for f in alternates]):
            # 2nd best (alternate name - for all foreign languages)
            score += 100000
            exact_match = True
        if name in codes:
            if (name.upper() == nmatch['country'] or name.upper() in alternates) and fc.startswith('PCL'):
                score += 50000
            elif (name.upper() == nmatch['admin1'] or name.upper() in alternates) and fc == 'ADM1':
                score += 50000
            else:
                score = -1
        # Give higher scores to political capitals.
        # 'Moscow' is most likely the one in Russia, not Maine
        if score > 50000:
            if fc:
                if fc.startswith(u'PCL'):
                    score += 100000
                elif fc == 'ADM1':
                    score += 50000
                elif fc.startswith('PPLA') and exact_match:
                    score += 40000
                elif fc == 'PPLC':
                    score += 400000
        if score > 1:
            if codes:
                admin = nmatch['admin1']
                if admin and admin in codes:
                    score *= 4
                admin = nmatch['country']
                if admin and admin in codes:
                    score *= 2
            if alternates:
                # Bonus points for actually having alternate names
                score += 5000
            if nmatch['feature_code'].startswith('PPL'):
                score += ((nmatch['population'] + 1) / 10)
            else:
                score += ((nmatch['population'] + 1) / 1000)
        nmatch['score'] = score

