def panda_query(names, codes):
    """
    Performs a query against Geonames Panda DataFrame.
    :param names: list of placenames to search for
    :param codes: list of potential admin/country codes to search for
    :return: matching geonames records
    """

    featurecodes = ['PCL','PCLD','PCLF','PCLI','PCLS','TERR',
                        'ADM1','ADM2','ADM3','ADM4',
                        'PPL','PPLA','PPLA2','PPLA3','PPLA4','PPLC'
                        ]
    featureclasses = ['P', 'A']
    population = 1000

    place_matches = {}
    admin_matches = {}
    country_matches = {}

    for name in names:
        result_pdd = geocode_pdd[
            (geocode_pdd["name"] == name.lower()) &
            (geocode_pdd["feature_class"].isin(featureclasses)) &
            (geocode_pdd["feature_code"].isin(featurecodes)) &
            (geocode_pdd["population"] > population)
         ]

        results = result_pdd.T.to_dict().values()
        name_matches = name_match(name, results)

        score_matches(name_matches, name, codes)
        for nmatch in name_matches:
            featurecode = nmatch['feature_code']
            if featurecode and featurecode.startswith('ADM'):
                admin_matches.setdefault(name, []).append(nmatch)
            elif featurecode and featurecode.startswith('PCL'):
                country_matches.setdefault(name, []).append(nmatch)
            else:
                place_matches.setdefault(name, []).append(nmatch)
    return place_matches, admin_matches, country_matches

%pyspark
def get_geonames(names, codes):
    """
    Find matching geonames records given a list of placenames and (possibly blank) state/country codes
    """
    all_matches = {}
    place_matches, admin_matches, country_matches = panda_query(names, codes)
    relation_score(place_matches, admin_matches, country_matches)

    for group in (place_matches, admin_matches, country_matches):
        unique_ids = []
        for k, v in group.iteritems():
            ranked = sorted(v, key=lambda k: k['score'], reverse=True)
            if ranked[0]['score'] > 1 and \
                            ranked[0]['geonameid'] not in unique_ids and \
                    (k not in all_matches or all_matches[k]['score'] <
                        ranked[0]['score']):
                all_matches[k] = ranked[0]
                unique_ids.append(ranked[0]['geonameid'])

    place_list = []
    for place in sorted(all_matches.values(), key=lambda k: k['score'], reverse=True):
        place_list.append({
                        'place_name': place['name'],
                        'place_lat': place['coord'][0][1],
                        'place_lng': place['coord'][0][0],
                        'geo_geonameid': int(place['geonameid'])
                    })   
    return place_list

def geocode(name):
    return get_geonames([name],[])

