def extract(text, lang='en', dumb_tokens=False, stanford=False):
    """
    Extract possible placenames from text, then query Geonames dataframe for matches.
    """
    name_counts = OrderedDict([])
    code_counts = {}

    if not text:
        # Nothing to do
        return

    if type(text) == str:
        text = unicode(text)

    text = re.sub(DELETE_REGEX, u', ', text)
    text = re.sub(r'\'', u'\'\'', text)

    # Look for state/country codes (2-3 capital letters)
    possible_codes = list(
        set(re.findall(CODE_REGEX, text)))
    for code in possible_codes:
        if code.lower() not in IGNORE_WORDS:
            code_counts[code.lower()] = code_counts.get(
                code, 0) + 1

    possible_names = []
    if lang in nospace_langs:
        # No spaces in language, so make spaces
        possible_names = splitchars(text)[0:255]
    elif dumb_tokens:
        # Split words on spaces
        possible_names = dumb_gpes(text)
    else:
        # Use NLTK tokenizer and Stanford tagger to find placenames.
        if stanford:
            try:
                possible_names.extend(get_stanford_gpes(text))
            except OSError:
                possible_names.extend(get_nltk_gpes(text))
        else:
            possible_names.extend(get_nltk_gpes(text))
            name_prefixes = ['san', 'las', 'los']
            for prefix in name_prefixes:
                san_matches = re.findall('{} \w+'.format(prefix), text, flags=re.IGNORECASE)
                for san_match in san_matches:
                    if san_match not in possible_names:
                        possible_names.append(san_match)
                    for san_word in san_match.split(' '):
                        if san_word in possible_names:
                            possible_names.remove(san_word)

    for i, possible_geoname in enumerate(possible_names):
        if possible_geoname.lower() in IGNORE_WORDS: continue

        # If it looks like a state/country code, treat it that way
        if (2 <= len(
                possible_geoname) <= 3) and possible_geoname.isupper() and \
                        lang not in nospace_langs:
            code_counts[possible_geoname.lower()] = code_counts.get(
                possible_geoname, 0) + 1
        else:
            name_counts[possible_geoname.lower()] = name_counts.get(
                possible_geoname.lower(), 0) + 1

    if not name_counts and not code_counts:
        # Nothing to do
        return []

    codes = u','.join(u'"{}"'.format(key) for key in code_counts.keys())

    names = name_counts.keys()
    names.extend(code_counts.keys())

    return get_geonames(names, codes)
